<script>
  import { createEventDispatcher } from "svelte";
  import Modal from "../components/modal.svelte";
  import { ArweaveWebWallet } from "arweave-wallet-connector";

  import { profile } from "../store.js";

  export let open;
  const dispatch = createEventDispatcher();

  async function arconnect() {
    if (!window.arweaveWallet) {
      window.open("https://arconnect.io");
    }
    await arweaveWallet.connect(
      ["ACCESS_ADDRESS", "SIGN_TRANSACTION", "DISPATCH"],
      { name: "stamp" }
    );
    const addr = await arweaveWallet.getActiveAddress();
    $profile = { addr };
    open = false;
    dispatch("connected");
  }

  async function arwallet() {
    const wallet = new ArweaveWebWallet({
      name: "stamps",
      url: window.location.href,
    });
    wallet.setUrl("arweave.app");
    await wallet.connect();

    const addr = await arweaveWallet.getActiveAddress();
    $profile = { addr };
    open = false;
    dispatch("connected");
  }
</script>

<Modal
  bind:open
  bgColor="bg-white"
  border="border-4 border-[#929292]"
  cancel={true}
  on:cancel={() => (open = false)}
>
  <div data-theme="light" class="px-[36px] py-[24px] flex flex-col space-y-8">
    <svg
      width="55"
      height="55"
      viewBox="0 0 55 55"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      xmlns:xlink="http://www.w3.org/1999/xlink"
    >
      <rect width="55" height="55" fill="url(#pattern0)" />
      <defs>
        <pattern
          id="pattern0"
          patternContentUnits="objectBoundingBox"
          width="1"
          height="1"
        >
          <use xlink:href="#image0_103_888" transform="scale(0.002)" />
        </pattern>
        <image
          id="image0_103_888"
          width="500"
          height="500"
          xlink:href="data:image/png;base64,"
        />
      </defs>
    </svg>
    <h2 class="text-2xl font-bold text-[#160042]">
      Connect with an Arweave Wallet to Stamp
    </h2>
    <p class="text-xl text-[#160042]">Select your preferred wallet below:</p>
    <button
      class="btn btn-block rounded-full hover:bg-gray-400"
      on:click={arconnect}>ArConnect</button
    >
    <button
      on:click={arwallet}
      class="btn btn-block rounded-full bg-[#E4E6F1] text-black hover:bg-gray-400"
      >Arweave.app</button
    >

    <a
      href="https://arweave.app/add"
      target="_blank"
      class="link no-underline text-center text-[#160042]"
      >I don't have a wallet</a
    >
  </div>
</Modal>
